mapper.update:
UPDATE recoms_like
SET likes = 'N'
WHERE recoms_idx = #{idx} AND user_name = #{userName}

mapper.select:
SELECT COUNT(*) as cnt
FROM recoms_like
WHERE recoms_idx = #{idx} AND user_name = #{userName}

mapper.update:
UPDATE recoms_like
SET likes = 'Y'
WHERE recoms_idx = #{idx} AND user_name = #{userName}

mapper.select:
SELECT movie_id
FROM recom
WHERE idx = #{idx} ORDER BY num;
